import React, { Component } from "react";
import {
  Grid,
  Segment,
  Label,
  Button,
  Input,
  Container,
  Header,
  Table
} from "semantic-ui-react";

class App extends Component {
  //WARNING! To be deprecated in React v17. Use componentDidMount instead.
  constructor(props) {
    super(props);
    this.state = {
      data24hrs: [],
      data7days: [],
      databeyond7days: [],
      errorMessage: "",
      isDataFound: false,
      tableData: []
    };

    this.applysearch = () => {
      this.setState({
        isDataFound: false
      });
      let URL = document.getElementById("repo-url").value;
      let message = "";
      if (URL) {
        fetch(URL, {
          Method: "GET"
        })
          .then(data => {
            return data.json();
          })
          .catch(function() {
            message = "Incorrect URL or response XML";
          })
          .then(response => {
            let now = new Date().getTime();
            const oneDay = 1000 * 60 * 60 * 24;
            const sevenDay = 1000 * 60 * 60 * 24 * 7;
            let db7 = [];
            let d7 = [];
            let d24 = [];
            for (var key in response) {
              if (response.hasOwnProperty(key)) {
                var val = response[key];
                let d = new Date(val.updated_at);
                if (val.state === "open") {
                  if (now - d.getTime() > sevenDay) {
                    db7.push(val);
                  } else if (now - d.getTime() > oneDay) {
                    d7.push(val);
                  } else {
                    d24.push(val);
                  }
                }
              }
            }
            this.setState({
              isDataFound: true,
              databeyond7days: db7,
              data7days: d7,
              data24hrs: d24,
              errorMessage: message,
              tableData: []
            });
          });
      }
    };

    this.onClickLabel = (e, data) => {
      let tableData = [];
      switch (data.id) {
        case "b7d":
          tableData = this.state.databeyond7days;
          break;
        case "d7":
          tableData = this.state.data7days;
          break;
        case "d24":
          tableData = this.state.data24hrs;
          break;
        default:
          tableData = [];
      }
      this.setState(
        {
          tableData
        },
        () => {
          console.log("data size::" + tableData.length);
        }
      );
    };
  }

  render() {
    let tableData = this.state.tableData;
    return (
      <div className="root">
        <Segment>
          <Input
            fluid
            id="repo-url"
            placeholder="Repository URL"
            label={
              <Button type="submit" onClick={this.applysearch} primary>
                Submit
              </Button>
            }
            labelPosition="right"
          />
          {this.state.errorMessage && <Label>{this.state.errorMessage}</Label>}
        </Segment>

        {this.state.isDataFound && (
          <Container centered>
            <Grid fluid columns={3} centered textAlign="center">
              <Header centered>Open Issues</Header>
              <Grid.Row centered style={{ "padding-left": "150px" }}>
                <Grid.Column>Beyond Last 7 Days</Grid.Column>
                <Grid.Column>In Last 7 Days</Grid.Column>
                <Grid.Column>In Last 24 hrs</Grid.Column>
              </Grid.Row>
              <Grid.Row centered style={{ "padding-left": "150px" }}>
                <Grid.Column centered>
                  <Label
                    id="b7d"
                    circular
                    size="massive"
                    color="red"
                    as="a"
                    onClick={this.onClickLabel}
                  >
                    {this.state.databeyond7days.length}
                  </Label>
                </Grid.Column>
                <Grid.Column centered>
                  <Label
                    id="d7"
                    circular
                    size="massive"
                    color="orange"
                    as="a"
                    onClick={this.onClickLabel}
                  >
                    {this.state.data7days.length}
                  </Label>
                </Grid.Column>
                <Grid.Column centered>
                  <Label
                    id="d24"
                    circular
                    size="massive"
                    color="yellow"
                    as="a"
                    onClick={this.onClickLabel}
                  >
                    {this.state.data24hrs.length}
                  </Label>
                </Grid.Column>
              </Grid.Row>
            </Grid>
            <Segment>
              <Table striped color="blue">
                <Table.Body>
                  <Table.Row>
                    <Table.Cell>
                      <Header>S.No.</Header>
                    </Table.Cell>
                    <Table.Cell>
                      <Header>IssueID</Header>
                    </Table.Cell>
                    <Table.Cell>
                      <Header> Issue Title</Header>
                    </Table.Cell>
                    <Table.Cell>
                      <Header> Created</Header>
                    </Table.Cell>
                    <Table.Cell>
                      <Header> Last Updated</Header>
                    </Table.Cell>
                  </Table.Row>

                  {tableData && Object.keys(tableData).length > 0 ? (
                    tableData.map((data, index) => (
                      <Table.Row>
                        <Table.Cell>{index + 1}</Table.Cell>
                        <Table.Cell>{data.number}</Table.Cell>
                        <Table.Cell>{data.title}</Table.Cell>
                        <Table.Cell>{data.created_at}</Table.Cell>
                        <Table.Cell>{data.updated_at}</Table.Cell>
                      </Table.Row>
                    ))
                  ) : (
                    <Table.Cell textAlign="center" colSpan="10">
                      No Record Found
                    </Table.Cell>
                  )}
                </Table.Body>
              </Table>
            </Segment>
          </Container>
        )}
      </div>
    );
  }
}

export default App;
