import React, { Component } from "react";
import { Form, Segment, Container, Button, Input } from "semantic-ui-react";

class App extends Component {
  //WARNING! To be deprecated in React v17. Use componentDidMount instead.
  constructor(props) {
    super(props);
    this.state = {
      response: []
    };

    this.applysearch = () => {
      let URL = document.getElementById("repo-url").innerText;
      console.log("url is:" + URL);
    };
  }

  componentWillMount() {
    fetch("https://api.github.com/repos/octocat/Hello-World/issues", {
      Method: "GET"
    })
      .then(data => {
        return data.json();
      })
      .then(response => {
        console.log("data is::" + JSON.stringify(response));
        this.setState({
          response
        });
      });
  }

  render() {
    return (
      <div className="root">
        <Segment>
          <Form>
            <Form.Field required>
              <Input id="repo-url" placeholder="Repository URL" />
            </Form.Field>
            <Button type="submit" onClick={this.applysearch}>
              Submit
            </Button>
          </Form>
        </Segment>
      </div>
    );
  }
}

export default App;
