import React, { Component } from "react";

import MainPage from "./MainPage";
import Footer from "./Footer";
import { ReactComponent } from "*.svg";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      response: [],
      data24hrs: [],
      data7days: [],
      databeyond7days: [],
      errorMessage: "",
      isDataFound: false,
      tableData: [],
      selectedLabel: ""
    };
    /**
     * Method to fetch json data from gethub repository
     */
    this.applysearch = () => {
      this.setState({
        isDataFound: false
      });
      let URL = document.getElementById("repo-url").value;
      let message = "";
      if (URL) {
        if (URL.indexOf("https://github.com/") === 0) {
          URL = URL.replace(
            "https://github.com/",
            "https://api.github.com/repos/"
          );
          URL = URL + "/issues";
        } else {
          this.setState({
            errorMessage: "Incorrect URL "
          });
          return;
        }

        fetch(URL, {
          Method: "GET"
        })
          .then(data => {
            return data.json();
          })
          .catch(function() {
            message = "Incorrect URL or response XML";
          })
          .then(response => {
            let now = new Date().getTime();
            const oneDay = 1000 * 60 * 60 * 24;
            const sevenDay = 1000 * 60 * 60 * 24 * 7;
            let db7 = [];
            let d7 = [];
            let d24 = [];
            for (var key in response) {
              if (response.hasOwnProperty(key)) {
                var val = response[key];
                let d = new Date(val.created_at);
                if (val.state === "open") {
                  if (now - d.getTime() > sevenDay) {
                    db7.push(val);
                  } else if (now - d.getTime() > oneDay) {
                    d7.push(val);
                  } else {
                    d24.push(val);
                  }
                }
              }
            }
            this.setState({
              isDataFound: true,
              response,
              databeyond7days: db7,
              data7days: d7,
              data24hrs: d24,
              errorMessage: message,
              tableData: []
            });
          });
      }
    };

    this.onClickLabel = (e, data) => {
      let tableData = [];
      let selectedLabel = "";
      switch (data.id) {
        case "total":
          tableData = this.state.response;
          selectedLabel = "Total Open Issues";
          break;
        case "b7d":
          tableData = this.state.databeyond7days;
          selectedLabel = "Open Issues Beyond 7 Days";
          break;
        case "d7":
          tableData = this.state.data7days;
          selectedLabel = "Open Issues In Last 7 Days";
          break;
        case "d24":
          tableData = this.state.data24hrs;
          selectedLabel = "Open Issues In Last 24 Hrs";
          break;
        default:
          tableData = [];
          selectedLabel = "";
      }
      this.setState({
        tableData,
        selectedLabel
      });
    };
  }
  componentDidMount() {
    document.title = "Dashboard";
  }

  render() {
    return (
      <div>
        <ReactComponent />
        <MainPage />
        <Footer />
      </div>
    );
  }
}

export default App;
