import React, { Component } from "react";
import {
  Grid,
  Segment,
  Label,
  Button,
  Input,
  Container,
  Header,
  Table
} from "semantic-ui-react";

class App extends Component {
  //WARNING! To be deprecated in React v17. Use componentDidMount instead.
  constructor(props) {
    super(props);
    this.state = {
      response: [],
      data24hrs: [],
      data7days: [],
      databeyond7days: [],
      errorMessage: "",
      isDataFound: false
    };

    this.applysearch = () => {
      this.setState({
        isDataFound: false
      });
      let URL = document.getElementById("repo-url").value;
      let message = "";
      if (URL) {
        fetch(URL, {
          Method: "GET"
        })
          .then(data => {
            return data.json();
          })
          .catch(function() {
            message = "Incorrect URL or response XML";
          })
          .then(response => {
            let now = new Date().getTime();
            const oneDay = 1000 * 60 * 60 * 24;
            const sevenDay = 1000 * 60 * 60 * 24 * 7;
            let db7 = [];
            let d7 = [];
            let d24 = [];
            for (var key in response) {
              if (response.hasOwnProperty(key)) {
                var val = response[key];
                let d = new Date(val.updated_at);
                if (val.state === "open") {
                  if (now - d.getTime() > sevenDay) {
                    db7.push(val);
                  } else if (now - d.getTime() > oneDay) {
                    d7.push(val);
                  } else {
                    d24.push(val);
                  }
                }
              }
            }
            this.setState({
              response,
              isDataFound: true,
              databeyond7days: db7,
              data7days: d7,
              data24hrs: d24,
              errorMessage: message
            });
          });
      }
    };
  }

  render() {
    return (
      <div className="root">
        <Segment>
          <Input
            fluid
            id="repo-url"
            placeholder="Repository URL"
            label={
              <Button type="submit" onClick={this.applysearch}>
                Submit
              </Button>
            }
            labelPosition="right"
          />
          {this.state.errorMessage && <Label>{this.state.errorMessage}</Label>}
        </Segment>

        {this.state.isDataFound && (
          <Container centered>
            <Grid columns={3} container centered>
              <Header centered style={{ "padding-top": "15px" }}>
                Open Issues
              </Header>
              <Grid.Row>
                <Grid.Column>Beyond Last 7 Days</Grid.Column>
                <Grid.Column>In Last 7 Days</Grid.Column>
                <Grid.Column>In Last 24 hrs</Grid.Column>
              </Grid.Row>
              <Grid.Row>
                <Grid.Column>
                  <Label circular size="massive" color="red">
                    {this.state.databeyond7days.length}
                  </Label>
                </Grid.Column>
                <Grid.Column>
                  <Label circular size="massive" color="orange">
                    {this.state.data7days.length}
                  </Label>
                </Grid.Column>
                <Grid.Column>
                  <Label circular size="massive" color="yellow">
                    {this.state.data24hrs.length}
                  </Label>
                </Grid.Column>
              </Grid.Row>
            </Grid>
            <Segment>
              <Table>
                <Table.Row>
                  <Table.Cell />
                  <Table.Cell />
                  <Table.Cell />
                  <Table.Cell />
                </Table.Row>
              </Table>
            </Segment>
          </Container>
        )}
      </div>
    );
  }
}

export default App;
