import React, { Component } from "react";
import { Form, Segment, Label, Button, Input } from "semantic-ui-react";

class App extends Component {
  //WARNING! To be deprecated in React v17. Use componentDidMount instead.
  constructor(props) {
    super(props);
    this.state = {
      response: [],
      data24hrs: [],
      data7days: [],
      databeyond7days: []
    };

    this.applysearch = () => {
      let URL = document.getElementById("repo-url").value;
      if (URL) {
        fetch(URL, {
          Method: "GET"
        })
          .then(data => {
            return data.json();
          })
          .then(response => {
            console.log("data is::" + JSON.stringify(response));
            let now = new Date();
            const oneDay = 60 * 60 * 24;
            const sevenDay = 60 * 60 * 24 * 7;
            let db7 = [];
            let d7 = [];
            let d24 = [];
            for (var key in response) {
              if (response.hasOwnProperty(key)) {
                var val = response[key];
                let d = new Date(val.updated_at);
                console.log(d);
                console.log(val.state);
                if (now - d > sevenDay) {
                  db7.push(val);
                  console.log("more than 7 days older");
                } else if (now - d > oneDay) {
                  console.log("7 days older");
                  d7.push(val);
                } else {
                  d24.push(val);
                  console.log("one day older", now - d);
                }
              }
            }
            this.setState({
              response,
              databeyond7days: db7,
              data7days: d7,
              data24hrs: d24
            });
          });
      }
    };
  }

  componentWillMount() {}

  render() {
    return (
      <div className="root">
        <Segment>
          <Form>
            <Form.Field required>
              <Input id="repo-url" placeholder="Repository URL" />
            </Form.Field>
            <Button type="submit" onClick={this.applysearch}>
              Submit
            </Button>
          </Form>
          <Label circular>{this.state.databeyond7days.length}</Label>
          <Label circular>{this.state.data7days.length}</Label>
          <Label circular>{this.state.data24hrs.length}</Label>
        </Segment>
      </div>
    );
  }
}

export default App;
